from Crypto.Util.number import getPrime, bytes_to_long


flag = bytes_to_long(open("flag", "rb").read().strip())

e = getPrime(128)
p = getPrime(1024)
q = getPrime(1024)
n = p * q

c = (flag * e) % n

print(f"e = {e}")
print(f"n = {n}")
print(f"c = {c}")

